const menuBtn=document.getElementById("menu-btn"),menuBtnImg=document.querySelector("#menu-btn .btn__img"),headerNav=document.querySelector(".header__nav");menuBtn.addEventListener("click",(()=>{headerNav.classList.toggle("menu-is-open"),headerNav.classList.contains("menu-is-open")?menuBtnImg.src="assets/images/icons/cross.svg":menuBtnImg.src="assets/images/icons/burger.svg"})),window.addEventListener("resize",(()=>{window.screen.width>1180&&(headerNav.classList.remove("menu-is-open"),menuBtnImg.src="assets/images/icons/burger.svg")}));